mapper.insert:
/** attach.addAttach **/
INSERT INTO attach ( 
	real_name
	, uuid_name
	, file_type
	, file_size
	, file_path
	, tb_name
	, tb_seq
	, tb_type
	, created_dt
	, created_no
) VALUES (
	#{realName}
	, #{uuidName}
	, #{fileType}
	, #{fileSize}
	, #{filePath}
	, #{tbName}
	, #{tbSeq}
	, #{tbType}
	, NOW()
	, #{memberSeq}
)


	SELECT LAST_INSERT_ID()

mapper.delete:
/** attach.removeAttach **/
DELETE FROM attach
WHERE attach_seq= #{attachSeq}
