mapper.select:
/** board.getBoards **/


ORDER BY board_seq desc

  	limit #{search.startRow}, #{search.size}

/** board.getBoardsCnt **/
select count(*) as cnt 
from board where 1=1

mapper.insert:
/** board.addBoard **/
INSERT INTO board (
	type_cd
	, lang_cd
	, title
	, content
	, remark
	, period_start_dt
	, period_end_dt
	, comment_yn
	, attachment_yn
	, use_yn
	, del_yn
	, created_dt
	, created_no
) VALUES (
	#{handle.typeCd}
	, #{handle.langCd}
	, #{title}
	, #{handle.content}
	, #{handle.remark}
	, #{handle.periodStartDt}
	, #{handle.periodEndDt}
	, #{commentYn}
	, #{attachmentYn}
	, #{handle.useYn}
	, #{handle.delYn}
	, NOW()
	, #{handle.memberSeq}
)

mapper.update:
/** board.setBoard **/ UPDATE board
title= #{title}, attachment_yn= #{attachmentYn}, comment_yn= #{commentYn},
type_cd= #{handle.typeCd}, lang_cd= #{handle.langCd}, content= #{handle.content}, remark= #{handle.remark}, period_start_dt= #{periodStartDt}, period_end_dt= #{periodEndDt}, use_yn= #{handle.useYn}, del_yn= #{handle.delYn}, updated_no= #{handle.memberSeq},
updated_dt= NOW()
WHERE board_seq= #{boardSeq}

mapper.delete:
/** board.removeBoard **/
DELETE FROM board
WHERE board_seq= #{boardSeq}
