mapper.insert:
/** comment.addComment **/
INSERT INTO comment (
	post_fk
	, member_fk
	, parents_no
	, deps_no
	, comment
	, created_dt
	, created_no
) VALUES (
	#{postSeq}
	, #{memberSeq}
	, #{parentsNo}
	, #{depsNo}
	, #{comment}
	, NOW()
	, #{memberSeq}
)

mapper.update:
/** comment.setComment **/
UPDATE comment

	comment= #{comment},
	updated_dt= NOW(),
	updated_no= #{memberSeq}

WHERE comment_seq= #{commentSeq}

mapper.delete:
/** comment.removeComment **/
DELETE FROM comment
WHERE comment_seq= #{commentSeq}
