sql.where:
  	1=1

		and `role`= #{role}


		and id= #{id}


		and password= CONCAT('*', UPPER(SHA1(UNHEX(SHA1((#{password}))))))


		and password= CONCAT('*', UPPER(SHA1(UNHEX(SHA1((#{oldPassword}))))))


		and name= #{name}


		and email= #{email}


		and phone= #{phone}


		and social_yn= #{socialYn}


		and del_yn= #{delYn}


		and member_seq= #{memberSeq}

mapper.select:
/** member.getMembers **/


ORDER BY member_seq desc

	limit #{startRow}, #{size}

mapper.insert:
  		/** member.addMember **/
  		INSERT INTO member ( 
	  		id
	  		, password
	  		, name
	  		, `role`
	  		, profile
	  		, email
	  		, phone
	  		, addr
	  		, remark
	  		, social_yn
	  		, del_yn
	  		, created_dt
	  	) VALUES (
  			#{id}
  			, CONCAT('*', UPPER(SHA1(UNHEX(SHA1((#{password}))))))
  			, #{name}
  			, #{role}
  			, #{profile}
  			, #{email}
  			, #{phone}
  			, #{addr}
  			, #{remark}
  			, #{socialYn}
  			, 'N'
  			, NOW()
  		)

  			 SELECT LAST_INSERT_ID()

mapper.update:
/** member.setMember **/ UPDATE member
name= #{name}, password= CONCAT('*', UPPER(SHA1(UNHEX(SHA1((#{newPassword})))))), `role`= #{role}, profile= #{profile}, email= #{email}, phone= #{phone}, addr= #{addr}, remark= #{remark}, social_yn= #{socialYn}, del_yn= #{delYn}, updated_dt= NOW(), updated_no= #{memberSeq}
member_seq= #{memberSeq}

mapper.delete:
  		/** member.removeMember **/
  		DELETE FROM member
		WHERE member_seq= #{memberSeq}

mapper.insert:
/** member.addMemberDetail **/
INSERT INTO member_detail (
	member_fk
	, category
	, content
	, remark
	, created_dt
	, created_no
) VALUES (
	#{memberSeq}
	, #{category}
	, #{content}
	, #{remark}
	, NOW()
	, #{memberSeq}
)

mapper.delete:
  		/** member.removeMemberDetail **/
  		DELETE FROM member_detail
		WHERE member_fk= #{memberSeq}
