mapper.insert:
/** memo.addMemo **/
INSERT INTO memo (
	member_fk
	, memo_dt
	, content
	, remark
	, created_dt
	, created_no
) VALUES (
	#{memberSeq}
	, #{memoDt}
	, #{handle.content}
	, #{handle.remark}
	, NOW()
	, #{handle.memberSeq}
)

mapper.update:
/** memo.setMemo **/ UPDATE memo
memo_dt= #{memoDt},
content= #{handle.content}, remark= #{handle.remark}, updated_no= #{handle.memberSeq},
updated_dt= NOW()
WHERE memo_seq= #{memoSeq}

mapper.delete:
		/** memo.removeMemo **/
		DELETE FROM memo
  		WHERE memo_seq= #{memoSeq}
