mapper.insert:
/** post.addPost **/
INSERT INTO post (
	board_fk
	, member_fk
	, thumbnail
	, title
	, content
	, remark
	, period_start_dt
	, period_end_dt
	, notice_yn
	, secret_yn
	, use_yn
	, del_yn
	, view_count
	, created_dt
	, created_no
) VALUES (
	#{boardSeq}
	, #{handle.memberSeq}
	, #{thumbnail}
	, #{title}
	, #{handle.content}
	, #{handle.remark}
	, #{handle.periodStartDt}
	, #{handle.periodEndDt}
	, #{noticeYn}
	, #{secretYn}
	, #{useYn}
	, #{delYn}
	, 0
	, NOW()
	, #{handle.memberSeq}
)

mapper.update:
/** post.setPost **/ UPDATE post
thumbnail= #{thumbnail}, title= #{title}, notice_yn= #{noticeYn}, secret_yn= #{secretYn},
content= #{handle.content}, remark= #{handle.remark}, period_start_dt= #{periodStartDt}, period_end_dt= #{periodEndDt}, use_yn= #{handle.useYn}, del_yn= #{handle.delYn}, updated_no= #{handle.memberSeq},
updated_dt= NOW()
WHERE post_seq= #{postSeq}

/** post.setPostForViewCount **/
UPDATE post SET
	view_count= view_count + 1,
	updated_dt= NOW()
WHERE post_seq= #{postSeq}

mapper.delete:
/** post.removePost **/
DELETE FROM post
WHERE post_seq= #{postSeq}
